package com.mycompany.authlib.views;import com.mycompany.authlib.controller.IAuthController;import com.mycompany.authlib.exceptions.IncorrectPasswordException;import com.mycompany.authlib.exceptions.UserAlreadyExistException;import com.mycompany.authlib.exceptions.UserNotFoundException;import com.mycompany.authlib.users.AuthenticatableUser;import com.mycompany.authlib.util.ConsoleAuthInputUtil;public class ConsoleAuthView implements IAuthView {    private final ConsoleAuthInputUtil inputReader;    private final IAuthController authController;        public ConsoleAuthView(IAuthController authController) {        inputReader = new ConsoleAuthInputUtil();        this.authController = authController;    }        @Override    public void handleRegistration() {        String username = inputReader.getUsername();        String password = inputReader.getPassword();        try {            authController.handleRegistration(username, password);            displaySuccess("Registration Successful.");        } catch (UserAlreadyExistException e) {            displayError("Username already Exist.");        }    }        @Override    public AuthenticatableUser handleLogin() {        String username = inputReader.getUsername();        String password = inputReader.getPassword();        try {            AuthenticatableUser user = authController.handleLogin(username, password);            displaySuccess("Login Successful.");            return user;        } catch (UserNotFoundException e) {            displayError("User not found");        } catch (IncorrectPasswordException e) {            displayError("Incorrect Password");        }        return null;    }    private void displaySuccess(String message) {        System.out.println("Success: " + message);    }        private void displayError(String message) {        System.out.println("Error: " + message);    }}