package com.mycompany.bankapplication4.models.users;import com.mycompany.authlib.users.AuthenticatableUser;import java.util.ArrayList;import java.util.List;import java.util.concurrent.atomic.AtomicLong;public class User implements AuthenticatableUser {    private static final AtomicLong idCounter = new AtomicLong(1000);        private final String username;    private String password;    private final String userId;    private final List<String> accountNumbers;        public User(String username, String password) {        this.username = username;        this.password = password;        this.userId = "CUST" + idCounter.incrementAndGet();        this.accountNumbers = new ArrayList<>();    }    @Override    public String getUsername() {        return username;    }        @Override    public boolean validatePassword(String enteredPassword) {        return this.password.equals(enteredPassword);    }        public void setPassword(String newPassword) {        this.password = newPassword;    }        public String getUserId() {        return userId;    }        public void addAccount(String accNo) {        accountNumbers.add(accNo);    }        public boolean hasAccount(String accNo) {        return accountNumbers.contains(accNo);    }    public List<String> getAccountNumbers() {        return new ArrayList<>(accountNumbers);    }}