package com.mycompany.authlib.views;import com.mycompany.authlib.controller.IAuthController;import com.mycompany.authlib.exceptions.IncorrectPasswordException;import com.mycompany.authlib.exceptions.UserAlreadyExistException;import com.mycompany.authlib.exceptions.UserNotFoundException;import com.mycompany.authlib.users.AuthenticatableUser;import java.util.Scanner;public class ConsoleAuthView implements IAuthView {    private final Scanner scanner;    private final IAuthController authController;        public ConsoleAuthView(IAuthController authController) {        scanner = new Scanner(System.in);        this.authController = authController;    }        @Override    public void handleRegistration() {        String username = getUsername();        String password = getPassword();        try {            authController.handleRegistration(username, password);            displayMessage("Registration Successful.");        } catch (UserAlreadyExistException e) {            displayError("Username already Exist.");        }    }        @Override    public AuthenticatableUser handleLogin() {        String username = getUsername();        String password = getPassword();        try {            AuthenticatableUser user = authController.handleLogin(username, password);            displayMessage("Login Successful.");            return user;        } catch (UserNotFoundException e) {            displayError("User not found");        } catch (IncorrectPasswordException e) {            displayError("Incorrect Password");        }        return null;    }        private String getUsername() {        System.out.print("Enter username: ");        return scanner.next();        }    private String getPassword() {        System.out.print("Enter password: ");        return scanner.next();    }    private void displayMessage(String message) {        System.out.println("Success: " + message);    }        private void displayError(String message) {        System.out.println("Error: " + message);    }}