package com.mycompany.bankapplication4.controllers;import com.mycompany.bankapplication4.exceptions.InsufficientAmountException;import com.mycompany.bankapplication4.exceptions.NotAMultipleOf5Exception;import com.mycompany.bankapplication4.models.cards.Card;public class ATMController implements IATMController {    private final Card card;    public ATMController(Card card) {        this.card = card;    }    @Override    public double getBalance() {        return card.getBalance();    }        @Override    public void handleDeposit(double amount) {         card.pay(amount);    }    @Override    public void handleWithdraw(double amount, double transactionCharge) throws NotAMultipleOf5Exception, InsufficientAmountException {        if(!isMultipleOf5(amount)) {            throw new NotAMultipleOf5Exception();        }                double totalDeduction = amount + transactionCharge;                if(!card.use(totalDeduction)) {            throw new InsufficientAmountException();        }    }    @Override    public void handleSwipe(double amount, double cashback) throws InsufficientAmountException {                double totalDeduction = amount - cashback;                if(!card.use(totalDeduction)) {            throw new InsufficientAmountException();        }     }        @Override    public double calculateTransactionCharge(double amount) {        if(amount <= 100) {            return amount * 0.02;        }        else {            return amount * 0.04;        }    }        @Override    public double calculateCashBack(double amount) {        return amount * 0.01;    }        private boolean isMultipleOf5(double amount) {        return amount % 5 == 0;    }}