package com.mycompany.bankapplication4.enums;import com.mycompany.bankapplication4.views.IATMView;public enum ATMOption {    DEPOSIT(1) {        @Override        public void execute(IATMView atmView) {            atmView.handleDeposit();        }    },    WITHDRAW(2) {        @Override        public void execute(IATMView atmView) {            atmView.handleWithdraw();        }    },    SWIPE(3) {        @Override        public void execute(IATMView atmView) {            atmView.handleSwipe();        }    },    EXIT(0) {        @Override        public void execute(IATMView atmView) {            atmView.handleExit();        }    },    INVALID(-1) {        @Override        public void execute(IATMView atmView) {            atmView.handleInvalidChoice();        }    };        private final int code;        ATMOption(int code) {        this.code = code;    }        public static ATMOption getOption(int code) {        for(ATMOption option : values()) {            if(option.code == code) {                return option;            }        }        return INVALID;    }        public abstract void execute(IATMView atmView);}