package com.mycompany.bankapplication4.enums;import com.mycompany.bankapplication4.views.IMainView;public enum MainOption {    REGISTER(1) {        @Override        public void execute(IMainView view) {            view.handleRegistration();        }    },    LOGIN(2) {        @Override        public void execute(IMainView view) {            view.handleLogin();        }    },    CARD(3) {        @Override        public void execute(IMainView view) {            view.handleCard();        }    },    EXIT(0) {        @Override        public void execute(IMainView view) {            view.stopRun();        }    },    INVALID(-1) {        @Override        public void execute(IMainView view) {            view.handleInvalidChoice();        }    };        private final int code;        MainOption(int code) {        this.code = code;    }        public static MainOption getOption(int code) {        for(MainOption option : values()) {            if(option.code == code) {                return option;            }        }        return INVALID;    }        public abstract void execute(IMainView view);}