package com.mycompany.bankapplication4.enums;import com.mycompany.bankapplication4.views.INetBankingView;public enum NetBankingOption {    LIST_ACCOUNTS(1) {        @Override        public void execute(INetBankingView netBankingView) {            netBankingView.handleListAccounts();        }    },    CREATE_ACCOUNT(2) {        @Override        public void execute(INetBankingView netBankingView) {            netBankingView.handleCreateAccount();        }    },    ISSUE_CARD(3) {        @Override        public void execute(INetBankingView netBankingView) {            netBankingView.handleIssueCard();        }    },    TRANSFER_FUND(4) {        @Override        public void execute(INetBankingView netBankingView) {            netBankingView.handleTransferFund();        }    },    LOGOUT(0) {        @Override        public void execute(INetBankingView netBankingView) {            netBankingView.handleLogout();        }    },    INVALID(-1) {        @Override        public void execute(INetBankingView netBankingView) {            netBankingView.handleInvalidChoice();        }    };        private final int code;        NetBankingOption(int code) {        this.code = code;    }        public static NetBankingOption getOption(int code) {        for(NetBankingOption option : values()) {            if(option.code == code) {                return option;            }        }        return INVALID;    }    public abstract void execute(INetBankingView netBankingView);}