package com.mycompany.bankapplication4.models.cards;import java.util.concurrent.atomic.AtomicLong;public abstract class Card {    private static final AtomicLong cardCounter = new AtomicLong(10000000);    private final String cardNo;    private final int pin;    public Card(int pin) {        this.cardNo = String.valueOf(cardCounter.incrementAndGet());        this.pin = pin;    }        public String getCardNo() {        return cardNo;    }        public boolean validatePin(int enteredPin) {        return this.pin == enteredPin;    }        public abstract boolean use(double amount);        public abstract void pay(double amount);        public abstract double getBalance();}