package com.mycompany.bankapplication4.controllers;import com.mycompany.bankapplication4.models.User;import com.mycompany.bankapplication4.repositories.IUserRepository;public class AuthController implements IAuthController {    private final IUserRepository users;    public AuthController(IUserRepository userRepository) {        this.users = userRepository;    }        @Override    public void handleRegistration(String username, String password) throws Exception {        if(users.isUsernameTaken(username)) {            throw new Exception("Username already exists");        }                User newUser = new User(username, password);        users.saveUser(newUser);    }    @Override    public User handleLogin(String username, String password) throws Exception {        User user = users.findUser(username);        if(user == null) {            throw new Exception("Username not found.");        } else if(!user.validatePassword(password)) {            throw new Exception("Incorrect Password.");        }         return user;    }}