package com.mycompany.bankapplication4.controllers;import com.mycompany.bankapplication4.models.users.AuthenticatableUser;import com.mycompany.bankapplication4.models.users.factory.AuthenticatableUserFactory;import com.mycompany.bankapplication4.repositories.IUserRepository;public class AuthController implements IAuthController {    private final IUserRepository users;    private final AuthenticatableUserFactory factory;    public AuthController(IUserRepository userRepository, AuthenticatableUserFactory factory) {        this.users = userRepository;        this.factory = factory;    }        @Override    public void handleRegistration(String username, String password) throws Exception {        if(users.isUsernameTaken(username)) {            throw new Exception("Username already exists");        }                AuthenticatableUser newUser = factory.createUser(username, password);        users.saveUser(newUser);    }    @Override    public AuthenticatableUser handleLogin(String username, String password) throws Exception {        AuthenticatableUser user = users.findUser(username);        if(user == null) {            throw new Exception("Username not found.");        } else if(!user.validatePassword(password)) {            throw new Exception("Incorrect Password.");        }         return user;    }}