package com.mycompany.bankapplication4.enums;public enum ATMOption {    DEPOSIT(1),    WITHDRAW(2),    SWIPE(3),    EXIT(0),    INVALID(-1);        private final int code;        ATMOption(int code) {        this.code = code;    }        public static ATMOption getOption(int code) {        for(ATMOption option : values()) {            if(option.code == code) {                return option;            }        }        return INVALID;    }}