package com.mycompany.bankapplication4.enums;import com.mycompany.bankapplication4.views.INetBankingView;public enum AccountType {    SAVINGS(1) {        @Override        public void createAccount(INetBankingView netBankingView) {            netBankingView.handleCreateSavingsAccount();        }    },    CURRENT(2) {        @Override        public void createAccount(INetBankingView netBankingView) {            netBankingView.handleCreateCurrentAccount();        }    },    INVALID(-1) {        @Override        public void createAccount(INetBankingView netBankingView) {            netBankingView.handleInvalidAccountChoice();        }    };        private final int code;        AccountType(int code) {        this.code = code;    }        public static AccountType getOption(int code) {        for(AccountType option : values()) {            if(option.code == code) {                return option;            }        }        return INVALID;    }        public abstract void createAccount(INetBankingView netBankingView);}