package com.mycompany.bankapplication4.enums;public enum AccountType {    SAVINGS(1),    CURRENT(2),    INVALID(-1);        private final int code;        AccountType(int code) {        this.code = code;    }        public static AccountType getOption(int code) {        for(AccountType option : values()) {            if(option.code == code) {                return option;            }        }        return INVALID;    }}