package com.mycompany.bankapplication4.enums;public enum CardType {    CREDIT(1),    DEBIT(2),    INVALID(-1);        private final int code;        CardType(int code) {        this.code = code;    }        public static CardType getOption(int code) {        for(CardType option : values()) {            if(option.code == code) {                return option;            }        }        return INVALID;    }}