package com.mycompany.bankapplication4.enums;import com.mycompany.bankapplication4.views.INetBankingView;public enum CardType {    CREDIT(1) {        @Override        public void issueCard(INetBankingView netBankingView) {            netBankingView.handleIssueCreditCard();        }    },    DEBIT(2) {        @Override        public void issueCard(INetBankingView netBankingView) {            netBankingView.handleIssueDebitCard();        }    },    INVALID(-1) {        @Override        public void issueCard(INetBankingView netBankingView) {            netBankingView.handleInvalidCardChoice();        }    };        private final int code;        CardType(int code) {        this.code = code;    }        public static CardType getOption(int code) {        for(CardType option : values()) {            if(option.code == code) {                return option;            }        }        return INVALID;    }        public abstract void issueCard(INetBankingView netBankingView);}