package com.mycompany.bankapplication4.enums;public enum NetBankingOption {    LIST_ACCOUNTS(1),    CREATE_ACCOUNT(2),    ISSUE_CARD(3),    TRANSFER_FUND(4),    LOGOUT(0),    INVALID(-1);        private final int code;        NetBankingOption(int code) {        this.code = code;    }        public static NetBankingOption getOption(int code) {        for(NetBankingOption option : values()) {            if(option.code == code) {                return option;            }        }        return INVALID;    }}