package com.mycompany.bankapplication4.models.accounts;import com.mycompany.bankapplication4.enums.AccountType;public class CurrentAccount extends BankAccount {    private static final double OVERDRAFT_LIMIT = 5000;        private int overdraftUsed;        public CurrentAccount(double initialAmount, String customerId) {        super(initialAmount, customerId);        this.overdraftUsed = 0;    }        @Override    public void deposit(double amount) {        double amountAfterPayOverdraft = payOverdraft(amount);        super.deposit(amountAfterPayOverdraft);    }    @Override    public boolean deduct(double amount) {        if(balance >= amount) {            balance -= amount;            return true;        } else if(useOverdraft(amount - balance)) {            balance = 0;            return true;        }        return false;    }        private double payOverdraft(double amount) {        if(amount > overdraftUsed) {            overdraftUsed = 0;            return amount - overdraftUsed;        } else {            overdraftUsed -= amount;            return 0;        }    }        private boolean useOverdraft(double amount) {        if(overdraftUsed + amount <= OVERDRAFT_LIMIT) {            overdraftUsed += amount;            return true;        }         return false;    }    @Override    public AccountType getAccountType() {        return AccountType.CURRENT;    }}