package com.mycompany.bankapplication4.views;import com.mycompany.bankapplication4.controllers.AuthController;import com.mycompany.bankapplication4.controllers.IAuthController;import com.mycompany.bankapplication4.models.User;import java.util.Scanner;public class AuthView {    Scanner scanner = new Scanner(System.in);    IAuthController authController = new AuthController();        public String getUsername() {        System.out.print("Enter username: ");        return scanner.next();        }    public String getPassword() {        System.out.print("Enter password: ");        return scanner.next();    }    public void displayMessage(String message) {        System.out.println(message);    }        public void displayError(String message) {        System.out.println("Error: " + message);    }    public void handleRegistration() {        String username = getUsername();        String password = getPassword();        try {            authController.handleRegistration(username, password);            displayMessage("Registration Successful.");        } catch (Exception e) {            displayError(e.getMessage());        }    }        public void handleLogin() {        String username = getUsername();        String password = getPassword();        try {            User user = authController.handleLogin(username, password);            displayMessage("Login Successful.");            NetBankingView netBankingView = new NetBankingView();                    } catch (Exception e) {            displayError(e.getMessage());        }    }}