package com.mycompany.bankapplication4.views;import com.mycompany.bankapplication4.controllers.IMainController;import com.mycompany.bankapplication4.enums.MainOption;import java.util.Scanner;public class ConsoleMainView implements IMainView {    private final Scanner scanner;    private final IMainController mainController;        private boolean running;        public ConsoleMainView(IMainController mainController) {        scanner = new Scanner(System.in);        this.mainController = mainController;    }        @Override    public void runMainView() {        running = true;        while(running) {            MainOption choice = getMainChoice();            choice.execute(this);        }        displayMessage("Exiting the Application");    }    private MainOption getMainChoice() {        System.out.println("1. New User Registration");        System.out.println("2. Net Banking Login");        System.out.println("3. Use Card");        System.out.println("0. Exit");                System.out.print("Enter choice: ");        return MainOption.getOption(scanner.nextInt());    }        @Override    public void handleRegistration() {        mainController.handleRegistration();    }        @Override    public void handleLogin() {        mainController.handleLogin();    }        @Override    public void handleCard() {        mainController.handleCard();    }        @Override    public void stopRun() {        running = false;    }        @Override    public void handleInvalidChoice() {        displayError("Invalid Choice.");    }        private void displayMessage(String message) {        System.out.println(message);    }    private void displayError(String message) {        System.out.println("Error: " + message);    }}