package com.mycompany.bankapplication4.views;import com.mycompany.bankapplication4.enums.MainOption;import java.util.Scanner;public class ConsoleMainView implements IMainView {    Scanner scanner = new Scanner(System.in);    public boolean running;        public void runView() {        running = true;        while(running) {            MainOption choice = getMainChoice();            choice.execute(this);        }    }    private MainOption getMainChoice() {        System.out.println("1. New User Registration");        System.out.println("2. Net Banking Login");        System.out.println("3. Use Card");        System.out.println("0. Exit");                System.out.print("Enter choice: ");        return MainOption.getOption(scanner.nextInt());    }        @Override    public void handleRegistration() {        AuthView authView = new AuthView();        authView.handleRegistration();    }        @Override    public void handleLogin() {        AuthView authView = new AuthView();        authView.handleLogin();    }        @Override    public void handleCard() {        ATMView atmView = new ATMView();        atmView.runView();    }        @Override    public void stopRun() {        running = false;    }        @Override    public void handleInvalidChoice() {        displayError("Invalid Choice.");    }    private void displayError(String message) {        System.out.println("Error: " + message);    }}