package com.mycompany.bankapplication4.views;import com.mycompany.bankapplication4.enums.AccountType;import com.mycompany.bankapplication4.enums.CardType;import com.mycompany.bankapplication4.enums.NetBankingOption;import com.mycompany.bankapplication4.models.accounts.BankAccount;import java.util.List;import java.util.Scanner;public class NetBankingView {    Scanner scanner = new Scanner(System.in);    public NetBankingOption getNetBankingMenuChoice() {        System.out.println("1. My Accounts");        System.out.println("2. Create Account");        System.out.println("3. Get ATM Card");        System.out.println("4. Transfer Funds");        System.out.println("0. Logout");                System.out.print("Enter choice: ");        return switch(scanner.nextInt()) {            case 1 -> NetBankingOption.LIST_ACCOUNTS;            case 2 -> NetBankingOption.CREATE_ACCOUNT;            case 3 -> NetBankingOption.ISSUE_CARD;            case 4 -> NetBankingOption.TRANSFER_FUND;            case 0 -> NetBankingOption.LOGOUT;            default -> NetBankingOption.INVALID;        };      }    public AccountType getAccountType() {        System.out.println("1. Savings Account");        System.out.println("2. Current Account");                System.out.print("Enter choice for account: ");        return switch(scanner.nextInt()) {            case 1 -> AccountType.SAVINGS;            case 2 -> AccountType.CURRENT;            default -> AccountType.INVALID;        };    }    public CardType getCardType() {        System.out.println("1. Credit Card");        System.out.println("2. Debit Card");                System.out.print("Enter choice for card: ");        return switch(scanner.nextInt()) {            case 1 -> CardType.CREDIT;            case 2 -> CardType.DEBIT;            default -> CardType.INVALID;        };    }        public String getAccountNumber() {        System.out.print("Enter account number: ");        return scanner.next();    }    public String getCardNumber() {        System.out.print("Enter card number: ");        return scanner.next();    }    public int getPin() {        System.out.print("Enter pin: ");        return scanner.nextInt();    }    public double getInitialDepositAmount() {        System.out.print("Enter initial deposit amount: ");        return scanner.nextDouble();    }        public String getSenderAccountNumber() {        System.out.print("Enter sender account number: ");        return scanner.next();    }    public String getReceiverAccountNumber() {        System.out.print("Enter receiver account number: ");        return scanner.next();    }    public double getTransferFundAmount() {        System.out.print("Enter transfer fund amount: ");        return scanner.nextDouble();        }    public void displayAccountList(List<BankAccount> accounts) {        for(BankAccount account : accounts) {            System.out.println("Account Number: " + account.getAccNo() + " Account Type: " + account.getAccountType());        }    }    public void displayBalance(double balance) {        System.out.println("Balance: " + balance);    }    public void displayMessage(String message) {        System.out.println(message);    }        public void displayError(String message) {        System.out.println("Error: " + message);    }  }